import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack,Form, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';



export class Drop_2 extends React.Component{

  constructor(){
    super();
    this.state = {
        selectedValue:''
       }
    this.handleChange = this.handleChange.bind(this);
  }

renderSelectedForm(param) {
switch(param) {
  case 'form_name1':
    return <Form name="form_name1" id="form_name1" >
          form 1
          </Form>;

  case 'form_name2':
    return <Form name="form_name1" id="form_name2" >
          form 2
          </Form>;

  case 'form_name3':
    return <Form name="form_name3" id="form_name3" >
          form 3
          </Form>;

case 'form_name4':
    return <Form name="form_name4" id="form_name4" >
          form 3
          </Form>;

  case 'form_name5':
    return <Form name="form_name5" id="form_name5" >
          form 1
          </Form>;

  case 'form_name6':
    return <Form name="form_name6" id="form_name6" >
          form 2
          </Form>;

  case 'form_name7':
    return <Form name="form_name7" id="form_name7" >
          form 3
          </Form>;

case 'form_name8':
    return <Form name="form_name8" id="form_name8" >
          form 3
          </Form>;

case 'form_name9':
  return <Form name="form_name9" id="form_name9" >
        form 2
        </Form>;

case 'form_name10':
  return <Form name="form_name10" id="form_name10" >
        form 3
        </Form>;

case 'form_name11':
  return <Form name="form_name11" id="form_name11" >
        form 3
        </Form>;


  default:
    return null;
}
}

handleChange(event) { this.setState({selectedValue: event.target.value}); }

render() {
return (
  <div>
    <div>
            <form >
              <select value={this.state.selectedValue} onChange={this.handleChange}>
                  <option value="" selected="selected"></option>
                  <option value="form_name1">Text Field</option>
                  <option value="form_name2">Text Area</option>
                  <option value="form_name3">Date</option>
                  <option value="form_name4">Multiple Select</option>
                  <option value="form_name5">Multiple Checkbox Select with Images</option>
                  <option value="form_name6">Dropdown</option>
                  <option value="form_name7">Yes/No</option>
                  <option value="form_name8">Single Radio Select with Images</option>
                  <option value="form_name9">Static Text</option>
                  <option value="form_name10">Term of Usage</option>
                  <option value="form_name11">Province</option>
              </select>
           </form>
    {this.renderSelectedForm(this.state.selectedValue)}
  </div>
  </div>
);
} 
}
