import React, {Fragment, useCallback, useState} from "react";
import {Card, Form, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import './AttributeCreation';

const options = [
    {id:"Company", text: 'Your company', value:'Your company',},
    {id:"How", text: 'How did you find us?',  value: 'How did you find us?'},
    {id:"Register",text: 'You register as', value: 'You register as'},
    {id:"Visitor",text: 'Dear visitor!', value: 'Dear visitor!'},
    {id:"DateBirth",text: 'Date of birth', value: 'Date of birth'},
    {id:"Country",text: 'Country', value: 'Country'},
    {id:"How3",text: 'How did you find us 3?', value: 'How did you find us 3?'},
    {id:"Email",text: 'Accept Marketing Email', value: 'Accept Marketing Email'},
    {id:"Interests",text: 'Interests', value: 'Interests'},
  ];



 

export class Drop_1 extends React.Component{

 
  render(){
   


  return(
    
  <>

  <Form>
      <Card sectioned title="Attribute Properties">
      <hr/>
      <TextField
        label="Label*:"
        placeholder="Your Company"
      />
      </Card>
</Form>

<Dropdown>
  <Dropdown.Toggle variant="success" id="dropdown-basic">
    Dropdown Button
  </Dropdown.Toggle>

  <Dropdown.Menu>
    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
  </Dropdown.Menu>
</Dropdown>
      
           


  

</>
    )

  }
}
