import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import './AttributeCreation';

function mostrar(id) {
  if (id == "estudiante") {
    $("#estudiante").show();
    $("#trabajador").hide();
    $("#autonomo").hide();
    $("#paro").hide();
}

if (id == "trabajador") {
    $("#estudiante").hide();
    $("#trabajador").show();
    $("#autonomo").hide();
    $("#paro").hide();
}

if (id == "autonomo") {
    $("#estudiante").hide();
    $("#trabajador").hide();
    $("#autonomo").show();
    $("#paro").hide();
}

if (id == "paro") {
    $("#estudiante").hide();
    $("#trabajador").hide();
    $("#autonomo").hide();
    $("#paro").show();
  }
return(
  <>
  <div id="estudiante" style="display: none;">
    <h2>Si eres estudiante...</h2>
    <form action="index.php" method="post">
        <p>Nombre:<br/>
        <input type="text" name="nombre" /></p>
        <p>Centro:<br/>
        <input type="text" name="centro" /></p>
        <input type="submit" name="send" value="Enviar" />
    </form>
</div>
<div id="trabajador" style="display: none;">
    <h2>Si eres estudiante...</h2>
    <form action="index.php" method="post">
        <p>Nombre:<br/>
        <input type="text" name="nombre" /></p>
        <p>Centro:<br/>
        <input type="text" name="centro" /></p>
        <input type="submit" name="send" value="Enviar" />
    </form>
</div>
<div id="autonomo" style="display: none;">
    <h2>Si eres estudiante...</h2>
    <form action="index.php" method="post">
        <p>Nombre:<br/>
        <input type="text" name="nombre" /></p>
        <p>Centro:<br/>
        <input type="text" name="centro" /></p>
        <input type="submit" name="send" value="Enviar" />
    </form>
</div>
<div id="paro" style="display: none;">
    <h2>Si eres estudiante...</h2>
    <form action="index.php" method="post">
        <p>Nombre:<br/>
        <input type="text" name="nombre" /></p>
        <p>Centro:<br/>
        <input type="text" name="centro" /></p>
        <input type="submit" name="send" value="Enviar" />
    </form>
</div>

<form action="index.php" method="post">
    Estado actual: 
    <select id="status" name="status" onChange="mostrar(this.value);">
        <option value="estudiante">Estudiante</option>
        <option value="trabajador">Trabajador</option>
        <option value="autonomo">Autónomo</option>
        <option value="paro">En el paro</option>
     </select>
</form>
</>
);
}

export class Drop_1 extends React.Component{



  render(){
  
        

    const options = [
      {id:"Company", label: 'Your company', value:'Your company'},
      {id:"How",label: 'How did you find us?',  value: 'How did you find us?'},
      {id:"Register",label: 'You register as', value: 'You register as'},
      {id:"Visitor",label: 'Dear visitor!', value: 'Dear visitor!'},
      {id:"DateBirth",label: 'Date of birth', value: 'Date of birth'},
      {id:"Country",label: 'Country', value: 'Country'},
      {id:"How3",label: 'How did you find us 3?', value: 'How did you find us 3?'},
      {id:"Email",label: 'Accept Marketing Email', value: 'Accept Marketing Email'},
      {id:"Interests",label: 'Interests', value: 'Interests'},
    ];
  return(
  <>
    <Dropdown  options={options} onChange={this.value}  placeholder="Choose an option" /> 
      
    
  
</>
    );

  }
}
