import React, {Fragment} from "react";
import {Card, Button, EmptyState} from "@shopify/polaris";



 class Index extends React.Component {
  render() {
    return (
      <>
      <Card sectioned title="Dashboard" actions={[{content: 'Add variant'}]}>
          <p>
            Home
          </p>
      </Card>

      <EmptyState
        heading="Discount your products temporarily"
        action={{
          content: 'Select products',
          onAction: () => console.log('clicked'),
        }}
        ></EmptyState>
      
<Card>
<Card.Section title="Application Stats">

<Card.Section title="Total Customers">
  <p>0</p>
  
</Card.Section>

<Card.Section title="Total Orders">
  <p>0</p>
</Card.Section>

<Card.Section title="Total Attributes">
  <p>0</p>
</Card.Section>

</Card.Section>
</Card>

</>

    );  
  }
}


export default Index;
