import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import AttCompany from './AttributeCompany';


export class Drop_1 extends React.Component{

  constructor(props){
    super(props);
    this.state ={
      dia: 'Martes'
    }
    this.cambioDia = this.cambioDia.bind(this);
  

    const options = [
      {label: 'Your company', value: <AttCompany/>},
      {label: 'How did you find us?', value: 'How did you find us?'},
      {label: 'You register as', value: 'You register as'},
      {label: 'Dear visitor!', value: 'Dear visitor!'},
      {label: 'Date of birth', value: 'Date of birth'},
      {label: 'Country', value: 'Country'},
      {label: 'How did you find us 3?', value: 'How did you find us 3?'},
      {label: 'Accept Marketing Email', value: 'Accept Marketing Email'},
      {label: 'Interests', value: 'Interests'},
    ];
  return(
    <Dropdown value={this.state.dia}  onChange={this.cambioDia}  placeholder="Choose an option" > 
      <Option>lunes </Option>
      <Option>martes</Option>
    </Dropdown>
 
   
    );

  }
}
