import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';



export class Drop_2 extends React.Component{
  render(){
    
    const options2= [
      {label: 'Text Field', value: 'Text Field'},
      {label: 'Text Area', value: 'Text Area'},
      {label: 'Date', value: 'Date'},
      {label: 'Multiple Select', value: 'Multiple Select'},
      {label: 'Multiple Checkbox Select with Images', value: 'Multiple Checkbox Select with Images'},
      {label: 'Dropdown', value: 'Dropdown'},
      {label: 'Yes/No', value: 'Yes/No'},
      {label: 'Single Radio Select with Images', value: 'Single Radio Select with Images'},
      {label: 'Static Text', value: ' Static Text'},
      {label: 'Term of usage', value: 'Term of usage'},
      {label: 'Province', value: 'Province'},
      
    ]
  return(
    <Dropdown  options={options2} onChange={this._onSelect}  placeholder="Text Field" />
  );
  }
}
