import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page} from "@shopify/polaris";
import { DropDownList } from '@progress/kendo-react-dropdowns';




 class AC extends React.Component {

  templates=["1","2","3"];

  render() {
    return (
      
<>
  
<Card sectioned title=" Create attribute" actions={[{content: 'Add variant'}]}>
          <p>
          Home Attributes Attribute
          </p>
      </Card>
      <Page>





    <Card sectioned title="Create attribute">
              
    <DropDownList data={this.templates}/>

    <p>If you choose a template, the following attribute fields will be filled automatically. Or you can leave this box blank and create your unique field by filling in these boxes.</p>
    <hr/>
    <p>Attribute Properties</p>

  
    
    </Card>


</Page>

</>

    );  
  }
}

export default AC;
