import React, {Fragment, useCallback, useState, useRef} from "react";
import {Card, Button,  EmptyState, Layout,Page, Stack, ActionList, AppProvider, Avatar, ContextualSaveBar, FormLayout,Frame, Loading,Modal, Navigation, SkeletonBodyText, SkeletonDisplayText, SkeletonPage, TextContainer, TextField, Toast, TopBar} from "@shopify/polaris";
import { Redirect } from '@shopify/app-bridge/actions';
import {ArrowLeftMinor, ConversationMinor, HomeMajorMonotone, OrdersMajorTwotone} from '@shopify/polaris-icons';



function ac() {
  <Redirect to='/AttributeCreation'/>;

}


 class Index extends React.Component {

 

  render() {
   
    return (
      <>
      
      <Card sectioned title="ashboard">
          <p>
            Home
          </p>
      </Card>
      <Page>

<Card>
<Card.Section title="Quickstart">
  <br/>
<Stack distribution="equalSpacing">
  <p>Create an attribute</p>  
  
  <Button primary onClick={ac}>Start Task</Button>
       
  </Stack>
</Card.Section>
</Card>


      
<Card>
<Card.Section title="Application Stats">

<Stack distribution="equalSpacing">

<Card.Section> 
  <p>Total Customers</p>
  <p>0</p>
  </Card.Section>
  
  <Card.Section>
  <p>Total Orders</p>
  <p>0</p>
  </Card.Section>

  <Card.Section>
  <p>Total Attributes</p>
  <p>0</p>
  </Card.Section>

</Stack>

</Card.Section>
</Card>
</Page>
</>

    );  
  }
}


export default Index;
