import React, {Fragment, useCallback, useState, useRef, Component} from "react";
import {Drop_1} from './Drop1';
import {Drop_2} from './Drop2';
import 'react-dropdown/style.css';
import {Card,Form, ChoiceList, TextStyle,Button, Heading,EmptyState, AppProvider,Layout,Page, Stack, ActionList, Avatar, ContextualSaveBar, FormLayout,Frame, Loading,Modal, Navigation, SkeletonBodyText, SkeletonDisplayText, SkeletonPage, TextContainer, TextField, Toast, TopBar, Link} from "@shopify/polaris";
import { Redirect } from '@shopify/app-bridge/actions';
import {ArrowLeftMinor, NoteMajorMonotone, HomeMajorMonotone, OrdersMajorTwotone, MobileHamburgerMajorMonotone, ListMajorMonotone, BankMajorMonotone,TroubleshootMajorMonotone,SettingsMajorMonotone} from '@shopify/polaris-icons';
import {mostrar} from './Drop1';
import { useForm } from 'react-hook-form'
import {v4 as uuidv4} from 'uuid';

const  AddAtributoForm = (props) => {
  
  const [Atributos, setAtributos] = useState(AtributosData);

  const{register, errors, handleSubmit } = useForm();

  const onSubmit = (data, e) =>{
    data.id = null
    console.log(data)
    props.addAtributo(data)
    e.target.reset();
  }

  const addAtributo = (Atributo) => {
    Atributo.id = uuidv4()
    setAtributos([...Atributos, Atributo])
  }

  const [textFieldValue, setTextFieldValue] = useState('');
  const handleTextFieldChange = useCallback(
    (value) => setTextFieldValue(value),
    [],
  );


  const [value, setValue] = useState('');
  const handleChangeCode = useCallback((newValue) => setValue(newValue), []);

  const [selected, setSelected] = useState(['hidden']);
  const handleChange = useCallback((value) => setSelected(value), []);
 
  

  const handleChangeNo = useCallback((newValueNo) => setValueNo(newValueNo), []);
  const [valueNo, setValueNo] = useState('1');



  
  {/*navbar */}


 return(
<>
  <Card sectioned title=" Create attribute">
    <Stack distribution="leading">
      <Link url='/Index' >
        Home
      </Link>
           
      <Link url='/componenteslist' >
        Attributes
      </Link>
        <p>Attribute</p>
    </Stack>      
  </Card>
        
<Page>
  <form onSubmit={handleSubmit(onSubmit)} id="inicial">
    <Card sectioned>     
      <TextStyle variation="strong">Use Template:</TextStyle>
        <Drop_1/>
   
      <br/>
      <Heading>Attribute Configuration</Heading>    
      <br/>
      <hr/>
  
       
        <ChoiceList
              allowMultiple
              choices={[
                {
                  label: 'Show on Cart page:',
                  value: 'shipping',
                  helpText:
                    'Tick if you want to use this attribute on cart page.',
                },
                {
                  label: 'Show on Register page:',
                  value: 'confirmation',
                  helpText:
                    'Tick if you want to use this attribute on register page.',
                },
              ]}
              selected={selected}
              onChange={handleChange}/>
<br/>
<br/>
<Stack distribution="trailing">
<Button >Add new Atribute</Button>
</Stack>
      </Card>
      <br/>
    </form>
  </Page>
</>
  
 )
}

export default AddAtributoForm
