import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import './AttributeCreation';


const [selected, setSelected] = useState('newestUpdate');

const handleSelectChange = useCallback((value) => setSelected(value), []);

const options = [
    {key:"Company", text: 'Your company', value:'Your company',},
    {key:"How", text: 'How did you find us?',  value: 'How did you find us?'},
    {key:"Register",text: 'You register as', value: 'You register as'},
    {key:"Visitor",text: 'Dear visitor!', value: 'Dear visitor!'},
    {key:"DateBirth",text: 'Date of birth', value: 'Date of birth'},
    {key:"Country",text: 'Country', value: 'Country'},
    {key:"How3",text: 'How did you find us 3?', value: 'How did you find us 3?'},
    {key:"Email",text: 'Accept Marketing Email', value: 'Accept Marketing Email'},
    {key:"Interests",text: 'Interests', value: 'Interests'},
  ];

export class Drop_1 extends React.Component{


  render(){
   
    
  return(
    
  <>
  <Dropdown
    label="Sort by"
    labelInline
    options={options}
    onChange={handleSelectChange}
    value={selected}
/>
      
      <p>current value: {value}</p>
           


  

</>
    )

  }
}
