import 'react-dropdown/style.css';
import Dropdown from 'react-dropdown';
import React, {Fragment, Component, useCallback, useState, useRef} from "react";
import {Card,Heading, Button, DataTable ,Link,EmptyState, AppProvider,Layout,Page, Stack, ActionList,  Avatar, ContextualSaveBar, FormLayout,Frame, Loading,Modal, Navigation, SkeletonBodyText, SkeletonDisplayText, SkeletonPage, TextContainer, TextField, Toast, TopBar} from "@shopify/polaris";
import { Redirect } from '@shopify/app-bridge/actions';
import {ArrowLeftMinor, AddMajorMonotone, NoteMajorMonotone, HomeMajorMonotone, OrdersMajorTwotone, MobileHamburgerMajorMonotone, ListMajorMonotone, BankMajorMonotone,TroubleshootMajorMonotone,SettingsMajorMonotone} from '@shopify/polaris-icons';
import AttributeTable from './TablaComponentes'

const Lista = () => {

  const AtributosData = [
    { id: 1, code: 'company', label: 'company', Type: 'Text Field', sortingorder: 1, status:'on', cartpage:'Yes', resgisterpage:'Yes', accountpage:'No', ordergrid:'Yes', customergrid:'Yes'},
    { id: 2, code: 'company', label: 'company', Type: 'Text Field', sortingorder: 2, status:'on', cartpage:'Yes', resgisterpage:'Yes', accountpage:'No', ordergrid:'Yes', customergrid:'Yes'},
    { id: 3, code: 'company', label: 'company', Type: 'Text Field', sortingorder: 3, status:'on', cartpage:'Yes', resgisterpage:'Yes', accountpage:'No', ordergrid:'Yes', customergrid:'Yes'},
    { id: 4, code: 'company', label: 'company', Type: 'Text Field', sortingorder: 4, status:'on', cartpage:'Yes', resgisterpage:'Yes', accountpage:'No', ordergrid:'Yes', customergrid:'Yes'},
    { id: 5, code: 'company', label: 'company', Type: 'Text Field', sortingorder: 4, status:'on', cartpage:'Yes', resgisterpage:'Yes', accountpage:'No', ordergrid:'Yes', customergrid:'Yes'},
    { id: 6, code: 'company', label: 'company', Type: 'Text Field', sortingorder: 2, status:'on', cartpage:'Yes', resgisterpage:'Yes', accountpage:'No', ordergrid:'Yes', customergrid:'Yes'},

  ]
  const [Atributos, setAtributos] = useState(AtributosData)

  const addAtributo = (Atributo) => {
    Atributo.id = Atributos.length + 1
    setAtributos([...Atributos, Atributo])
  }

  const deleteAtributos = (id) => {
    setUsers(Atributos.filter((Atributo) => Atributo.id !== id))
  }

  return (
<>
    <Card sectioned title="Attributes">
       
    <Stack distribution="leading">
      <Link url='/Index' >
        Home
      </Link>         
        <p>Attributes</p>
    </Stack>             
    <Stack distribution="trailing">

        <Link url='/AttributeCreation'>
          <Button primary>
            <Stack distribution="equalSpacing">
              
              <p>New Attribute</p>
            </Stack>
          </Button>
        </Link> 

    </Stack>   
  </Card>

    <Card>
      <AttributeTable Atributos={Atributos}/>
    </Card>
    </>
  )
}

export default Lista
