import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';



export class Drop_2 extends React.Component{
  render(){
    
    const options2= [
      {id:"TextF",label: 'Text Field', value: 'Text Field'},
      {id:"TextA",label: 'Text Area', value: 'Text Area'},
      {id:"Date",label: 'Date', value: 'Date'},
      {id:"Select",label: 'Multiple Select', value: 'Multiple Select'},
      {id:"MultipleCheck",label: 'Multiple Checkbox Select with Images', value: 'Multiple Checkbox Select with Images'},
      {id:"Dropdown",label: 'Dropdown', value: 'Dropdown'},
      {id:"YesNo",label: 'Yes/No', value: 'Yes/No'},
      {id:"RadioImages",label: 'Single Radio Select with Images', value: 'Single Radio Select with Images'},
      {id:"StaticText",label: 'Static Text', value: ' Static Text'},
      {id:"Term",label: 'Term of usage', value: 'Term of usage'},
      {id:"Province",label: 'Province', value: 'Province'},
      
    ]
  return(
    <Dropdown  options={options2} onChange={this._onSelect}  placeholder="Text Field" />
  );
  }
}
