import React, {Fragment, useCallback, useState, useRef} from "react";
import 'react-dropdown/style.css';
import {Card,DatePicker, Tabs, Checkbox, Form, ChoiceList, Button, Heading,EmptyState, AppProvider,Layout,Page, Stack, ActionList,  Avatar, ContextualSaveBar, FormLayout,Frame, Loading,Modal, Navigation, SkeletonBodyText, SkeletonDisplayText, SkeletonPage, TextContainer, TextField, Toast, TopBar, Link} from "@shopify/polaris";
import { Redirect } from '@shopify/app-bridge/actions';
import {ArrowLeftMinor, NoteMajorMonotone, HomeMajorMonotone, OrdersMajorTwotone, MobileHamburgerMajorMonotone, ListMajorMonotone, BankMajorMonotone,TroubleshootMajorMonotone,SettingsMajorMonotone} from '@shopify/polaris-icons';
import Dropdown from 'react-dropdown';


export default function AttDateBirth(){


  const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = useCallback(
    (selectedTabIndex) => setSelectedTab(selectedTabIndex),
    [],
  );
  
  const tabs = [
    {
      id: 'FormDateB',
      content: 'Properties',
      accessibilityLabel: 'All customers',
      panelID: 'all-customers-content',
    },
   
  ];

  const [valueDate, setValueDate] = useState('');
  const handleChangeDate = useCallback((newValueDate) => setValueDate(newValueDate), []);

  const [selected, setSelected] = useState(['hidden']);
  const handleChange = useCallback((value) => setSelected(value), []);
 
  const [textFieldValue, setTextFieldValue] = useState('');
  const handleTextFieldChange = useCallback(
    (value) => setTextFieldValue(value),
    [],
  );

  const [value, setValue] = useState('');
  const handleChangePh = useCallback((newValue) => setValue(newValue), []);

  const [valueFormat, setValueFormat] = useState('');
  const handleChangeFornmat = useCallback((newValueFormat) => setValueFormat(newValueFormat), []);
  
  const handleChangeNo = useCallback((newValueNo) => setValueNo(newValueNo), []);
  const [valueNo, setValueNo] = useState('1');

  const [checked, setChecked] = useState(false);
  const handleChangeSingle = useCallback((newChecked) => setChecked(newChecked), []);
  
   {/*navbar */}


   const skipToContentRef = useRef(null);

   const [toastActive, setToastActive] = useState(false);
   const [isLoading, setIsLoading] = useState(false);
   const [isDirty, setIsDirty] = useState(false);
   const [searchActive, setSearchActive] = useState(false);
   const [searchValue, setSearchValue] = useState('');
   const [userMenuActive, setUserMenuActive] = useState(false);
   const [mobileNavigationActive, setMobileNavigationActive] = useState(false);
   const [modalActive, setModalActive] = useState(false);
   const [nameFieldValue, setNameFieldValue] = useState(
   );
   const [emailFieldValue, setEmailFieldValue] = useState(
   );
   const [storeName, setStoreName] = useState(
   );
   const [supportSubject, setSupportSubject] = useState('');
   const [supportMessage, setSupportMessage] = useState('');
 
   const handleSubjectChange = useCallback(
     (value) => setSupportSubject(value),
     [],
   );
   const handleMessageChange = useCallback(
     (value) => setSupportMessage(value),
     [],
   );
   const handleDiscard = useCallback(() => {
     setEmailFieldValue(defaultState.current.emailFieldValue);
     setNameFieldValue(defaultState.current.nameFieldValue);
     setIsDirty(false);
   }, []);
   const handleSave = useCallback(() => {
     defaultState.current.nameFieldValue = nameFieldValue;
     defaultState.current.emailFieldValue = emailFieldValue;
 
     setIsDirty(false);
     setToastActive(true);
     setStoreName(defaultState.current.nameFieldValue);
   }, [emailFieldValue, nameFieldValue]);
   const handleNameFieldChange = useCallback((value) => {
     setNameFieldValue(value);
     value && setIsDirty(true);
   }, []);
   const handleEmailFieldChange = useCallback((value) => {
     setEmailFieldValue(value);
     value && setIsDirty(true);
   }, []);
   const handleSearchResultsDismiss = useCallback(() => {
     setSearchActive(false);
     setSearchValue('');
   }, []);
   const handleSearchFieldChange = useCallback((value) => {
     setSearchValue(value);
     setSearchActive(value.length > 0);
   }, []);
   const toggleToastActive = useCallback(
     () => setToastActive((toastActive) => !toastActive),
     [],
   );
   const toggleUserMenuActive = useCallback(
     () => setUserMenuActive((userMenuActive) => !userMenuActive),
     [],
   );
   const toggleMobileNavigationActive = useCallback(
     () =>
       setMobileNavigationActive(
         (mobileNavigationActive) => !mobileNavigationActive,
       ),
     [],
   );
   const toggleIsLoading = useCallback(
     () => setIsLoading((isLoading) => !isLoading),
     [],
   );
   const toggleModalActive = useCallback(
     () => setModalActive((modalActive) => !modalActive),
     [],
   );
 
   const toastMarkup = toastActive ? (
     <Toast onDismiss={toggleToastActive} content="Changes saved" />
   ) : null;
 
   const userMenuActions = [
     {
       items: [{content: 'Community forums'}],
     },
   ];
   
 
   const contextualSaveBarMarkup = isDirty ? (
     <ContextualSaveBar
       message="Unsaved changes"
       saveAction={{
         onAction: handleSave,
       }}
       discardAction={{
         onAction: handleDiscard,
       }}
     />
   ) : null;
 
 
  
 
 
 
   const topBarMarkup = (
     <TopBar
       showNavigationToggle
       searchResultsVisible={searchActive}  
       onSearchResultsDismiss={handleSearchResultsDismiss}
       onNavigationToggle={toggleMobileNavigationActive}
     />
   );
 
   
   const navigationMarkup = (
     <Navigation location="/">
      
       <Navigation.Section
         separator
         title="Dashboard"
         items={[
 
           {
             url: './Index',
             label: 'Dashboard',
             icon: HomeMajorMonotone,
             onClick: toggleIsLoading,
           },
           {
             url: './componenteslist',
             label: 'Attributes',
             icon: MobileHamburgerMajorMonotone,
             onClick: toggleIsLoading,
           },
           {
             url: './AttributesTemplates',
             label: 'Attribute Templates',
             icon: BankMajorMonotone,
             onClick: toggleIsLoading,
           },
           {
             label: 'Theme Installer',
             icon: TroubleshootMajorMonotone,
             onClick: toggleIsLoading,
           },
         ]}
        
       />
     </Navigation>
   );
 
   const loadingMarkup = isLoading ? <Loading /> : null;
 
   const skipToContentTarget = (
     <a id="SkipToContentTarget" ref={skipToContentRef} tabIndex={-1} />
   );
 
   const actualPageMarkup = (
  
<>
  <Card sectioned title=" Attribute Date of birth">
    <Stack distribution="leading">
      <Link url='/Index' >
        Home
      </Link>
           
      <Link url='/AttributesTemplates' >
        Attributes Templates
      </Link>
        <p>Attribute</p>
    </Stack>        
  </Card>
        
<Page>             
  <Card>
    <Tabs tabs={tabs} selected={selectedTab} onSelect={handleTabChange}>
      <Card.Section >
        <Form>
          <Card sectioned title="Attribute Properties">
            <hr/>

            <TextField
              label="Label*:"
              value={textFieldValue}
              onChange={handleTextFieldChange}
              placeholder="Date of birth"/>
            <p>Attribute label</p>
            <br/>
        
            <TextField
              label="Code:"
              placeholder="date-of-birth"
              disabled/>
            <p>For internal use. Must be unique with 
              no spaces and length  &lt;= 30. Let 
              it empty for auto generation</p>
            <br/>
            
            <TextField
              label="Sorting Order:"
              type="number"
              value={valueNo}
              onChange={handleChangeNo}/>
            <p>Attributes are sorted in ascending 
              order (eg: Attribute with sorting order 
              1 will be displayed on top of Attribute 2)</p>
            <br/>
  
                <p>Type*:</p>
                  <Dropdown placeholder="Date" disabled />
                <br/>
   
            <TextField
              id="date"
              label="Default value:"
              type="date"
              defaultValue="DD-MM-YYYY"
              value={valueDate}
              onChange={handleChangeDate}/>
            <p>The default value for this attribute when it is shown to your customers</p>
            <br/>

            <TextField
              label="Placeholder:"
              value={value}
              onChange={handleChangePh}/>
            <p>The placeholder specifies a short hint 
                that describes the expected value of 
                this field</p>
            <br/>

            <Checkbox
                label="Values Required"
                checked={checked}
                onChange={handleChangeSingle}/>
            <p>Tick if this attribute is required to be 
                filled by your customers</p>
            <br/>

            
            <TextField
                label="Format Options:"
                value={valueFormat}
                onChange={handleChangeFornmat}/>
            <p>You need to chose default date again to 
                save with new format</p>
            <br/>

              <br/>
              <Heading>Attribute Configuration</Heading>    
              <br/>
              <hr/>
  
       
            <ChoiceList
                allowMultiple
                choices={[
                  {
                    label: 'Show on Cart page:',
                    value: 'shipping',
                    helpText:
                      'Tick if you want to use this attribute on cart page.',
                  },
                  {
                    label: 'Hide if filled:',
                    value: 'confirmation',
                    helpText:
                      'Hide this attribute if it was filled before.',
                  },
                  {
                    label: 'Show on Order grid:',
                    value: 'shipping',
                    helpText:
                      'Tick if you want to use this attribute on order grid in app admin.',
                  },
                  {
                    label: 'Show on Register page:',
                    value: 'confirmation',
                    helpText:
                      'Tick if you want to use this attribute on register page.',
                  },
                  {
                    label: 'Show on Customer grid',
                    value: 'confirmation',
                    helpText:
                      'Tick if you want to use this attribute on customer grid in app admin',
                  },
                ]}
                selected={selected}
                onChange={handleChange}/>
          </Card>
        </Form>  
      </Card.Section>
    </Tabs>
  </Card>
  <br/>
</Page>  
</>
  
  );

  const loadingPageMarkup = (
    <SkeletonPage>
      <Layout>
        <Layout.Section>
          <Card sectioned>
            <TextContainer>
              <SkeletonDisplayText size="small" />
              <SkeletonBodyText lines={9} />
            </TextContainer>
          </Card>
        </Layout.Section>
      </Layout>
    </SkeletonPage>
  );

  const pageMarkup = isLoading ? loadingPageMarkup : actualPageMarkup;

  const modalMarkup = (
    <Modal
      open={modalActive}
      onClose={toggleModalActive}
      title="Contact support"
      primaryAction={{
        content: 'Send',
        onAction: toggleModalActive,
      }}
    >
      <Modal.Section>
        <FormLayout>
          <TextField
            label="Subject"
            value={supportSubject}
            onChange={handleSubjectChange}
          />
          <TextField
            label="Message"
            value={supportMessage}
            onChange={handleMessageChange}
            multiline
          />
        </FormLayout>
      </Modal.Section>
    </Modal>
  );

  const theme = {
    colors: {
      topBar: {
        background: '#fff',
      },
    },
  };

  return (
    <div style={{height: '500px'}}>
      <AppProvider
        theme={theme}
        i18n={{
          Polaris: {
            Avatar: {
              label: 'Avatar',
              labelWithInitials: 'Avatar with initials {initials}',
            },
            ContextualSaveBar: {
              save: 'Save',
              discard: 'Discard',
            },
            TextField: {
              characterCount: '{count} characters',
            },
            TopBar: {
              toggleMenuLabel: 'Toggle menu',

              SearchField: {
                clearButtonLabel: 'Clear',
                search: 'Search',
              },
            },
            Modal: {
              iFrameTitle: 'body markup',
            },
            Frame: {
              skipToContent: 'Skip to content',
              Navigation: {
                closeMobileNavigationLabel: 'Close navigation',
              },
            },
          },
        }}
      >
        <Frame
          topBar={topBarMarkup}
          navigation={navigationMarkup}
          showMobileNavigation={mobileNavigationActive}
          onNavigationDismiss={toggleMobileNavigationActive}
          skipToContentTarget={skipToContentRef.current}
        >
          {contextualSaveBarMarkup}
          {loadingMarkup}
          {pageMarkup}
          {toastMarkup}
          {modalMarkup}
        </Frame>
      </AppProvider>
    </div>
  );
}
