import React, {Fragment} from "react";
import {Card, Button, SettingToggle} from "@shopify/polaris";



 class Index extends React.Component {
  render() {
    const [active, setActive] = useState(false);

    const handleToggle = useCallback(() => setActive((active) => !active), []);
  
    const contentStatus = active ? 'Disable' : 'Enable';
    const textStatus = active ? 'enabled' : 'disabled';
    return (
      <>
      <Card sectioned title="Dashboard" actions={[{content: 'Add variant'}]}>
          <p>
            Home
          </p>
      </Card>

      <SettingToggle
      action={{
        content: contentStatus,
        onAction: handleToggle,
      }}
      enabled={active}
    >
      This setting is <TextStyle variation="strong">{textStatus}</TextStyle>.
    </SettingToggle>


<Card>
<Card.Section title="Application Stats">

<Card.Section title="Total Customers">
  <p>0</p>
  
</Card.Section>

<Card.Section title="Total Orders">
  <p>0</p>
</Card.Section>

<Card.Section title="Total Attributes">
  <p>0</p>
</Card.Section>

</Card.Section>
</Card>

</>

    );  
  }
}


export default Index;
