import 'react-dropdown/style.css';
import Dropdown from 'react-dropdown';
import React, {Fragment, useCallback, useState, useRef} from "react";
import {Card, Button, DataTable ,Link,EmptyState, Layout,Page, Stack, ActionList,  Avatar, ContextualSaveBar, FormLayout,Frame, Loading,Modal, Navigation, SkeletonBodyText, SkeletonDisplayText, SkeletonPage, TextContainer, TextField, Toast, TopBar} from "@shopify/polaris";
import { Redirect } from '@shopify/app-bridge/actions';
import {ArrowLeftMinor, NoteMajorMonotone, HomeMajorMonotone, OrdersMajorTwotone, MobileHamburgerMajorMonotone, ListMajorMonotone, BankMajorMonotone,TroubleshootMajorMonotone,SettingsMajorMonotone} from '@shopify/polaris-icons';
import AppProvider from './navbar.js';


export class tabla_2 extends React.Component{
  render(){
  const rows2 =[['1', '1', 1,1 , '1'],];

  return(
<DataTable
          columnContentTypes={[
            'text',
            'text',
            'text',
            'text',
            'text',
          ]}
          headings={[
            'Cart page',
            'Register page',
            'Account page',
            'Order grid',
            'Customer grid',
          ]}
          rows={rows2}
        />
  );
}
}

export default function AttList(){



const rows = [
  ['1', '1', 1,1 , '1', <tabla_2/>, 1],
];


return (


  <>
     <Card sectioned title="Attributes">
  <Stack distribution="leading">
  <Link url='/Index' >
  Home
  </Link>
           
<p>
  Attributes
</p>
</Stack>
            
        </Card>
<Page>
      <Card sectioned>
        
        
        <br />
          <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Crear</Button>
          <br />
          <br />
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Personaje</th>
                <th>Anime</th>
                <th>Acci√≥n</th>
              </tr>
            </thead>

            <tbody>
              {this.state.data.map((dato) => (
                <tr key={dato.id}>
                  <td>{dato.id}</td>
                  <td>{dato.personaje}</td>
                  <td>{dato.anime}</td>
                  <td>
                    <Button
                      color="primary"
                      onClick={() => this.mostrarModalActualizar(dato)}
                    >
                      Editar
                    </Button>{" "}
                    <Button color="danger" onClick={()=> this.eliminar(dato)}>Eliminar</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
      </Card>
    </Page>
    </>
);
        }
