import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';




  export default class drop2 extends React.Component {
   

  render() {
    

 

    const options= [
      {label: '', value: 'y'},
      {label: '1', value: 'Your company'},
      {label: '2', value: 'How did you find us?'},
      
    ]
    

    return (
      <>
    

    <Drop id="drop2" options={options} onChange={this._onSelect} placeholder=""/>
    </>
    );  

  }
}
