import React, {Fragment, useCallback, useState} from "react";
import {Card, Button, Select, Page, Stack, TextField, Heading, ChoiceList,} from "@shopify/polaris";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';




 class drop extends React.Component {
   

  render() {
    

    const options = [
      {label: '', value: 'today'},
      {label: 'Your company', value: 'today'},
      {label: 'How did you find us?', value: 'yesterday'},
      {label: 'You register as', value: 'lastWeek'},
      {label: 'Dear visitor!', value: 'today'},
      {label: 'Date of birth', value: 'yesterday'},
      {label: 'Country', value: 'lastWeek'},
      {label: 'How did you find us 3?', value: 'today'},
      {label: 'Accept Marketing Email', value: 'yesterday'},
      {label: 'Interests', value: 'lastWeek'},
    ];

    

    return (
      
    <Dropdown options={options} onChange={this._onSelect}  placeholder="" />

    );  
  }
}

export default drop ;
